CREATE OR REPLACE FUNCTION jsonb_minus ( arg1 jsonb, arg2 jsonb, preserveField varchar, retainOnlyFields varchar ) RETURNS jsonb AS $$
SELECT COALESCE(json_object_agg(key, value), '{}')::jsonb FROM jsonb_each(arg1) WHERE ((arg1 -> key <> arg2 -> key OR arg2 -> key IS NULL) and key NOT IN ('version','created', 'updated', 'creator', 'updater') AND (retainOnlyFields ='' or position(','||key||',' IN ','||retainOnlyFields ||',') >0)) OR (preserveField <> '' and key = preserveField)
$$ LANGUAGE SQL;

CREATE OR REPLACE FUNCTION jsonb_removeNull ( arg1 jsonb, preserveField varchar, retainOnlyFields varchar ) RETURNS jsonb AS $$
SELECT COALESCE(json_object_agg(key, value), '{}')::jsonb FROM jsonb_each(arg1) WHERE value <> 'null' and value <> '{}' and key NOT IN ('version','created', 'updated', 'creator', 'updater') AND ((retainOnlyFields ='' or position(','||key||',' IN ','||retainOnlyFields ||',') >0) OR (preserveField <> '' and key = preserveField))
$$ LANGUAGE SQL;

CREATE OR REPLACE FUNCTION audit_data_log_trigger_func()
    RETURNS trigger AS $body$

    DECLARE
        audit_info varchar(400);
        audit_info_array text[];
        db_user varchar(100);
        origin varchar(15);  
        origin_name varchar(250);
        data_old jsonb;
        data_new jsonb;
        data_tmp jsonb;
        ref_id bigint;
        ref_table varchar(60);
        save boolean; 
    BEGIN

        save := true;

        audit_info := current_setting('var.opencell_audit_info', true);
        if (audit_info IS NOT NULL) then
            if (audit_info <> '') then
                audit_info_array := string_to_array(audit_info, '~~');
                db_user := audit_info_array[1];
                origin := audit_info_array[2];
                origin_name := audit_info_array[3];
            else
                origin :='GUI';
            end if;
         else 
            select current_user into db_user;
            origin :='DB';
        end if;

        if (TG_OP = 'INSERT') then
            data_new := to_jsonb(NEW);
            if (data_new ? 'id') then
                ref_id :=NEW.id;
            end if;                        
            data_new := jsonb_removeNull(data_new, TG_ARGV[0], TG_ARGV[1]);

            save := TG_ARGV[2]::bool or data_new <> '{}';

        elseif (TG_OP = 'UPDATE') then
            data_old := to_jsonb(OLD);
            data_new := to_jsonb(NEW);
            if (data_new ? 'id') then
                ref_id :=NEW.id;
            end if;
            data_tmp := jsonb_minus(data_new, data_old, TG_ARGV[0], TG_ARGV[1]);
            data_old := jsonb_minus(data_old, data_new, TG_ARGV[0], TG_ARGV[1]);
            data_new := data_tmp;

            save := TG_ARGV[2]::bool or data_new <> '{}';

        else 
            data_old := to_jsonb(OLD);
            if (data_old ? 'id') then
                ref_id :=OLD.id;
            end if;                        
            data_old := jsonb_removeNull(data_old, TG_ARGV[0], TG_ARGV[1]);

            save := TG_ARGV[2]::bool or data_old <> '{}';

        end if;

        if (save) then
            INSERT INTO audit_data_log_rec (
                id, created, tx_id, ref_table, ref_id, action, origin, origin_name, user_name, data_old, data_new )
            VALUES(
                nextval('audit_data_log_rec_seq'), statement_timestamp(), txid_current(), TG_TABLE_NAME, ref_id, TG_OP, origin, origin_name, db_user, data_old, data_new );
        end if;

        if (TG_OP = 'DELETE') then
            RETURN OLD;
        else 
            RETURN NEW;
        end if;
    END;
    $body$
    LANGUAGE plpgsql;

CREATE OR REPLACE procedure proc_recreate_audit_data_trigger(table_name varchar, fields varchar, actions varchar, preserveField varchar, saveEvenDiffIsEmpty boolean)
      language plpgsql
      as $$

      DECLARE
          trigger_name varchar(100);
          trigger_actions varchar(1000);
      BEGIN

  trigger_name :=table_name || '_audit_trigger';


  IF (actions IS NULL OR LOWER(actions) LIKE '%insert%') THEN
      trigger_actions := 'INSERT';
  END IF;

  IF (actions IS NULL OR LOWER(actions) LIKE '%update%') THEN
      trigger_actions := CASE WHEN trigger_actions IS NULL THEN '' ELSE trigger_actions || ' OR' END || ' UPDATE';
      if (fields IS NOT NULL) THEN
          trigger_actions := trigger_actions || ' of ' || fields;
      END IF;
  END IF;


  IF (actions IS NULL OR LOWER(actions) LIKE '%delete%') THEN
      trigger_actions := CASE WHEN trigger_actions IS NULL THEN '' ELSE trigger_actions || ' OR' END || ' DELETE';
  END IF;

  EXECUTE format ('DROP TRIGGER IF EXISTS %s ON %s', trigger_name, table_name );
  EXECUTE format ('CREATE TRIGGER %I AFTER %s ON %s FOR EACH ROW EXECUTE FUNCTION audit_data_log_trigger_func(''%s'',''%s'',%I)', trigger_name, trigger_actions, table_name, preserveField, fields, saveEvenDiffIsEmpty::bool );

  END$$;

CREATE OR REPLACE procedure proc_delete_audit_data_trigger(table_name varchar)
      language plpgsql
      as $$

      DECLARE
          trigger_name varchar(100);
      BEGIN

  trigger_name :=table_name || '_audit_trigger';

  EXECUTE format ('DROP TRIGGER IF EXISTS %s ON %s', trigger_name, table_name );

  END$$;

changeSet.sql:
CREATE OR REPLACE FUNCTION create_new_wo_partition(
    p_source_table    text,
    p_partition_name  text,
    p_range_from      date
) RETURNS void LANGUAGE plpgsql AS $$
DECLARE
    l_partition_interval  integer;
    l_range_to            date;
BEGIN

    SELECT wo_partition_range_months
    INTO l_partition_interval
    FROM finance_settings limit 1;

    IF l_partition_interval IS NULL THEN
        RAISE EXCEPTION 'Partition interval setting not found for table: %', p_source_table;
    END IF;

    l_range_to = p_range_from + l_partition_interval * interval '1 month';

    IF EXISTS (SELECT 1 FROM tech_wo_partition_log t WHERE t.partition_name = p_partition_name) THEN
        RAISE EXCEPTION 'The partition name [%] already exists', p_partition_name;
    end if;

    IF EXISTS (SELECT 1 FROM tech_wo_partition_log t WHERE (t.range_from, t.range_to) OVERLAPS (p_range_from, l_range_to)) THEN
        RAISE EXCEPTION 'The period range [%, %[ already coverd by an existing partition', p_range_from, l_range_to;
    end if;

    execute format('create table %I partition of %I for values  FROM (%L) TO (to_date(%L, ''YYYY-MM-DD'') + %s * interval ''1 month'')',
                   p_partition_name,
                   p_source_table,
                   p_range_from,
                   p_range_from,
                   l_partition_interval);

    execute format('insert into tech_wo_partition_log (partition_name, range_from, range_to) values (%L, %L, (to_date(%L, ''YYYY-MM-DD'') + %s * interval ''1 month''))',
                   p_partition_name,
                   p_range_from,
                   p_range_from,
                   l_partition_interval);
END;
$$;

